select o.order_no,o.order_time,o.report_no,o.car_number,o.car_frame_number,o.carModelNumberName, o.order_type,o.take_car_time,t.id,t.order_autoparts_status as orderStatus , t.partsTypeName,t.parts_name, t.partsCaregoryName,t.amount, (select ta.storage_path from car_scrap_order_attachment a,tb_attachment ta where a.attachment_id = ta.id and a.order_id=t.id and a.attachment_type='1' limit 0,1)as picture, case o.client_type when '1' then '个人' else (select c1.customer_name from sys_client c ,sys_company c1 where c.companyid = c1.id and c.id=o.client_id) end as custormName from( select o.car_model_number as carModelNumberName,o.* from car_scrap_order o ) o , ( select a.*,p.parts_name as partsTypeName,p.partsCaregoryName from car_scrap_order_autoparts a,(select p.*,c.parts_category_name as partsCaregoryName,c.id as partsCaregoryId from tb_car_parts p , tb_car_parts_category c where p.parts_category_id = c.id) p where a.parts_type= p.id) t where o.id = t.order_id and o.order_type='2' and o.isremove='0' and t.is_valid='1'
and (o.order_no like CONCAT('%',#{vo.keyWord},'%') or o.report_no like CONCAT('%',#{vo.keyWord},'%') or o.car_number like CONCAT('%',#{vo.keyWord},'%') or o.car_frame_number like CONCAT('%',#{vo.keyWord},'%') or o.take_car_contacts like CONCAT('%',#{vo.keyWord},'%') or o.take_car_contact_number like CONCAT('%',#{vo.keyWord},'%') or o.car_model_number like CONCAT('%',#{vo.keyWord},'%'))
and o.order_areas_id in
${item}
and t.order_autoparts_status in
${item}
and t.operatortime <=#{vo.earlyWarningDate}
order by t.operatortime desc

select count(*) from ( select v.carname as carModelNumberName,o.* from car_scrap_order o left join tb_car_vehicle v on o.car_model_number = v.id) o , ( select a.*,p.parts_name as partsTypeName,p.partsCaregoryName from ( select DISTINCT oa.* from car_scrap_order_autoparts oa , car_scrap_order_audit au where oa.id = au.order_id
and au.auditor=#{vo.userid}
and au.stauts=#{vo.orderStatus}
)a , ( select a.*,p.parts_name as partsTypeName,p.partsCaregoryName from car_scrap_order_autoparts a,(select p.*,c.parts_category_name as partsCaregoryName,c.id as partsCaregoryId from tb_car_parts p , tb_car_parts_category c where p.parts_category_id = c.id) p where a.parts_type= p.id) p where a.parts_type= p.id) t where o.id = t.order_id and o.order_type='2'
and (o.order_no like CONCAT('%',#{vo.keyWord},'%') or o.report_no like CONCAT('%',#{vo.keyWord},'%') or o.car_number like CONCAT('%',#{vo.keyWord},'%') or o.car_frame_number like CONCAT('%',#{vo.keyWord},'%') or o.take_car_contacts like CONCAT('%',#{vo.keyWord},'%') or o.take_car_contact_number like CONCAT('%',#{vo.keyWord},'%') or o.carModelNumberName like CONCAT('%',#{vo.keyWord},'%'))


