select (select a2.storage_path from car_scrap_order_attachment a1,tb_attachment a2 where a1.order_id=t.id and a1.attachment_id = a2.id and a1.car_picture_type is not null order by a1.car_picture_type limit 0,1) as picture, case t.client_type when '1' then '个人' else (select c1.customer_name from sys_client c ,sys_company c1 where c.companyid = c1.id and c.id=t.client_id) end as custormName, t.car_model_number as carModelNumberName, (select count(*) from car_scrap_order_autoparts a where a.order_id=t.id) as totalPartsCount,
t.* from ( select DISTINCT o.* from car_scrap_order o, car_scrap_order_audit a where o.id = a.order_id
and a.auditor=#{vo.userid}
and a.stauts= #{vo.orderStatus}
and o.order_type= #{vo.orderType}
) t where 1=1
and (t.order_no like CONCAT('%',#{vo.keyWord},'%') or t.report_no like CONCAT('%',#{vo.keyWord},'%') or t.car_number like CONCAT('%',#{vo.keyWord},'%') or t.car_frame_number like CONCAT('%',#{vo.keyWord},'%') or t.take_car_contacts like CONCAT('%',#{vo.keyWord},'%') or t.take_car_contact_number like CONCAT('%',#{vo.keyWord},'%') or t.car_model_number like CONCAT('%',#{vo.keyWord},'%'))
order by t.operatortime desc

